sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	// Get Short discription value from queryParams
	var incidentShortDiscValue = request.queryParams.inc_shortdisc;

	var inc = new GlideRecord('incident');
	inc.initialize();
	inc.short_description = 'Testing:: ' + incidentShortDiscValue;
	inc.caller_id = 'admin';
	//inc.u_affected_user.setDisplayValue   = 'Eashwar Elumalai';
	//inc.u_symptom = 'Error';
	inc.impact = 4; //Creating low impact incident
	inc.urgency = 4; //Creating low urgency ticket, Severity is auto populated
	//inc.setDisplayValue('assignment_group','SMO-Support');
	inc.description = 'Automated Incident Creation Testing - ' + incidentShortDiscValue;

	//Create incident record
	inc.insert();

	//gs.print("Incident Successfully Created");

	var body = {};
	body.msg = "Incident " + inc.number + " Successfully Created";
	// Send the response object, which is returned to the requestor, to the body object.
	response.setBody(body);


})(request, response);
